import PersonalDetails from "./PersonalDetails";
import Links from "./Links";
import styled from "styled-components";
import React,{useState} from "react"
import WorkExperience from "./WorkExperience"


function Editor(props) {
  const { userDetails, setUserDetails, links, setLinks } = props;
  const [PersonalDetailsSide, setPersonalDetailsSide] = useState(true);
  const [WorkExpSide, SetWorkExpSide] = useState(false);

  function handlePersonalDetails(){
    setPersonalDetailsSide(true)
    SetWorkExpSide(false)
  }
  function handleWorkExp(){
    SetWorkExpSide(true)
  }
  return (
    <>
    <SideBar>
       <PersonalDetailsSideBar>
         
         <input type="radio" 
         id = "PersonalDetails"
         name="selection"
         value="PersonalDetails"
        // checked="checked"
         onChange={handlePersonalDetails}/>
         <label for ="PersonalDetails">Personal Details</label>
       </PersonalDetailsSideBar>
       <WorkExpSideBar>
        
          <input type="radio" 
         id = "WorkExperience"
         value="WorkExperience"
         name="selection"
         onChange={handleWorkExp}/> 
          <label for="WorkExperience">Work Experience</label>
         </WorkExpSideBar>
    </SideBar>
   
    <EditorMapper>
      {PersonalDetailsSide && WorkExpSide==false && <PersonalDetails
        userDetails={userDetails}
        setUserDetails={setUserDetails}
        links={links}
        setLinks={setLinks}
      />}
      {WorkExpSide && <WorkExperience   userDetails={userDetails}
        setUserDetails={setUserDetails}
        links={links}
        setLinks={setLinks}/>}
    </EditorMapper>
    </>
  );
}
export default Editor;

const EditorMapper = styled.div`
border: 0.1rem solid #F178B6;
margin: 0 auto;
max-width: 35rem;
height: 40rem;
padding: 2rem 2rem;
`;
const SideBar = styled.div`
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  position: absolute;
 
`;
const PersonalDetailsSideBar = styled.div`
input{
  /* visibility: hidden; */
  pointer-events: none;
  
}
margin: 3rem 9.3rem;
position: relative;
border: 0.2rem solid  #F178B6;
background-color: #F178B6;
width: 15rem;
height:2rem;
border-radius: 27px 0px 0px 27px;
`;
const WorkExpSideBar = styled.div`
input{
  /* visibility: hidden; */
  pointer-events: none;
  
}
margin: 3rem 9.3rem;
position: relative;
border: 0.2rem solid  #F178B6;
background-color: #F178B6;
width: 15rem;
height:2rem;
border-radius: 27px 0px 0px 27px;
`;
